import {useEffect, useState} from "react";
import io from "socket.io-client";
import {v4} from "uuid";

const PORT = 3001;
const socket = io(`localhost:${PORT}`);

function App() {

    const [isConnected, setIsConnected] = useState(socket.connected);
    const [newMessage, setNewMessage] = useState("");
    const [user, setUser] = useState("");
    const [room, setRoom] = useState("");
    const [chatVisible, setChatVisible] = useState(false);
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        console.log('connected:', socket.connected)

        socket.on('connect', () => {
            setIsConnected(true);
        });

        socket.on('disconnect', () => {
            setIsConnected(false);
        });

        return () => {
            socket.off('connect');
            socket.off('disconnect');
        }
    }, [isConnected]);

    useEffect(() => {
        socket.on('receive_msg', ({user, message}) => {
            const msg = `${user}: ${message}`;
            setMessages(prevState => [msg, ...prevState]);
        });

        return () => {
            socket.off('receive_msg');
        };

    }, [socket])

    const handleEnterChatRoom = () => {
        if (user !== '' && room !== '') {
            setChatVisible(true)
            socket.emit('join_room', {user, room});
        }
    }

    const handleSendMessage = () => {
        const newMsgData = {
            room: room,
            user: user,
            message: newMessage,
        }

        socket.emit('send_message', newMsgData)
        const msg = `${user} send: ${newMessage}`
        setMessages(prevState => [msg, ...prevState]);
        setNewMessage("")
    }

    const inputStyles = {
        border: '1px solid #252525',
        padding: '5px',
        borderRadius: '5px',

    }

    return (
        <div style={{padding: 20}}>
            {!chatVisible ?
                <>
                    <input style={inputStyles} type="text" placeholder="user" value={user} onChange={e => setUser(e.target.value)}/>
                    <br/>
                    <input style={inputStyles} type="text" placeholder="room" value={room} onChange={e => setRoom(e.target.value)}/>
                    <br/>
                    <button onClick={handleEnterChatRoom}>Enter</button>
                </>
                :
                <>
                    <h5>Room: {room} | User: {user}</h5>
                    <div style={{
                        height: 200,
                        width: 250,
                        border: "1px solid #000",
                        overflowY: 'scroll',
                        marginBottom: 10,
                        padding: 10,
                    }}>
                        {messages.map(el => <div key={v4()}>{el}</div>)}
                    </div>
                    <input style={inputStyles} type="text"
                           placeholder="message"
                           value={newMessage}
                           onChange={e => setNewMessage(e.target.value)}
                    />
                    <button onClick={handleSendMessage}>Send message</button>
                </>
            }
        </div>
    )
}

export default App;
